window.addEventListener("DOMContentLoaded", function () {
  var i = 0,
    s = document.getElementById("nav-root"),
    e = s.clientHeight;
  window.addEventListener("scroll", function () {
    var t = -1 * document.body.getBoundingClientRect().top;
    t < i
      ? t > 0 && s.classList.contains("is-fixed")
        ? s.classList.add("is-visible")
        : s.classList.remove("is-visible", "is-fixed")
      : (s.classList.remove(["is-visible"]),
        t > e &&
          !s.classList.contains("is-fixed") &&
          s.classList.add("is-fixed")),
      (i = t);
  });
});
